import React from 'react';
import { AppBar, Toolbar, Typography, Button, Container, Box, Grid, Paper } from '@mui/material';
import HealthAndSafetyIcon from '@mui/icons-material/HealthAndSafety';
import { useNavigate } from 'react-router-dom';

const LandingPage = () => {
    const navigate = useNavigate();

    const handleSignUpClick = () => {
        navigate('/signup');
    };

    const handleLoginClick = () => {
        navigate('/login');
    };

    return (
        <Container maxWidth="lg">
            <AppBar position="static">
                <Toolbar>
                    <HealthAndSafetyIcon />
                    <Typography variant="h6" style={{ flexGrow: 1, marginLeft: 10 }}>
                        HealthWise Hospital Management
                    </Typography>
                    <Button color="inherit" onClick={handleSignUpClick} variant="outlined" style={{ marginRight: 10 }}>Sign Up</Button>
                    <Button color="inherit" onClick={handleLoginClick} variant="outlined">Log In</Button>
                </Toolbar>
            </AppBar>

            {/* Hero Section */}
            <Box
                sx={{
                    textAlign: 'center',
                    backgroundImage: 'url(/path/to/hero-image.jpg)',
                    backgroundSize: 'cover',
                    color: '#fff',
                    padding: '80px 0'
                }}
            >
                <Typography variant="h3" gutterBottom>
                    Welcome to HealthWise Hospital Management
                </Typography>
                <Typography variant="h6" sx={{ maxWidth: '600px', margin: '0 auto' }}>
                    Your trusted partner for seamless hospital management, empowering healthcare providers and patients alike.
                </Typography>
                <Button onClick={handleSignUpClick} variant="contained" color="primary" sx={{ marginTop: 4 }}>Get Started</Button>
            </Box>

            {/* Key Features */}
            <Grid container spacing={4} style={{ marginTop: '40px' }}>
                <Grid item xs={12} sm={4}>
                    <Paper elevation={3} style={{ padding: '20px', textAlign: 'center' }}>
                        <Typography variant="h6">Appointment Booking</Typography>
                        <Typography>Schedule appointments with ease.</Typography>
                    </Paper>
                </Grid>
                <Grid item xs={12} sm={4}>
                    <Paper elevation={3} style={{ padding: '20px', textAlign: 'center' }}>
                        <Typography variant="h6">Doctor Dashboard</Typography>
                        <Typography>Manage patient records and appointments.</Typography>
                    </Paper>
                </Grid>
                <Grid item xs={12} sm={4}>
                    <Paper elevation={3} style={{ padding: '20px', textAlign: 'center' }}>
                        <Typography variant="h6">Patient Management</Typography>
                        <Typography>View and update patient details.</Typography>
                    </Paper>
                </Grid>
            </Grid>
        </Container>
    );
};

export default LandingPage;
