import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from './LandingPage';
import SignUp from './auth/SignUp';
import Login from './auth/Login';
import AdditionalInfo from './auth/AdditionalInfo';
import PatientDashboard from './patient/PatientDashboard';
import AdminDashboard from './admin/AdminDashboard';
import DoctorDashboard from './doctor/DoctorDashboard';
import ProtectedRoute from './auth/ProtectedRoute'; // Import the ProtectedRoute component
import { UserProvider } from './auth/UserContext';

const AppRouter = () => {
    // Retrieve the JWT token and decode it to get the role 
    const token = sessionStorage.getItem('jwtToken');
    const userRole = token ? JSON.parse(atob(token.split('.')[1])).role : null; // Assuming role is in the JWT payload

    return (
        <Router>
            <UserProvider>
            <Routes>
                <Route path="/" element={<LandingPage />} />
                <Route path="/signup" element={<SignUp />} />
                <Route path="/login" element={<Login />} />
                <Route path="/additional-info" element={<AdditionalInfo />} />




                <Route path="/admin/dashboard" element={
                    <ProtectedRoute 
                        element={<AdminDashboard />} 
                        allowedRoles={['admin']} 
                        userRole={userRole} 
                    />} 
                />

                <Route path="/doctor/dashboard" element={
                    <ProtectedRoute 
                        element={<DoctorDashboard />} 
                        allowedRoles={['doctor']} 
                        userRole={userRole} 
                    />} 
                />
            </Routes>
            </UserProvider>
        </Router>
    );
};

export default AppRouter;
