import React, { useEffect, useState } from 'react'
import Navbar from './Navbar'
import { Box, Button, CircularProgress, Container, List, ListItem, ListItemText, Typography } from '@mui/material';

interface Appointment {
    id: number;
    date: string;
    time: string;
    patientId: Patient;
  }

interface Patient {
    name: string;
    age : number;

}

const DoctorDashboard = () => {
    const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const selectedDate = localStorage.getItem('selectedDate') || ''; // Get selected date from local storage

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        if (selectedDate) {
          const response = await fetch(`YOUR_BACKEND_API_ENDPOINT?date=${selectedDate}`); // Pass date as a query parameter
          const data = await response.json();
          setAppointments(data); // Adjust based on the shape of your response
        } else {
          console.error('No date selected');
        }
      } catch (error) {
        console.error('Error fetching appointments:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [selectedDate]);
  return (
    <>
        <Navbar />
      <Container sx={{ mt: 5 }}>
        <Typography variant="h4" gutterBottom>
          Appointments for {selectedDate}
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : (
          <List>
            {appointments.length > 0 ? (
              appointments.map((appointment) => (
                <ListItem key={appointment.id}>
                  <ListItemText
                    primary={`${appointment.time} - ${appointment.patient.name}`} // Patient's name
                    secondary={`Age: ${appointment.patient.age}`} // Patient's age
                  />
                </ListItem>
              ))
            ) : (
              <Typography variant="body1">No appointments scheduled for this date.</Typography>
            )}
          </List>
        )}
      </Container>
    </>
  );
};

export default DoctorDashboard
