import React, { useEffect, useState } from 'react';
import Navbar from './Navbar';
import { Box, Button, CircularProgress, Container, List, ListItem, ListItemText, Typography, Alert } from '@mui/material';

interface Appointment {
    id: number;
    date: string;
    time: string;
    patient: Patient;
}

interface Patient {
    id: string;
    name: string;
    age: number;
}

const DoctorDashboard: React.FC = () => {
    const [appointments, setAppointments] = useState<Appointment[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null); // State for error handling
    const selectedDate = new Date().toISOString().split('T')[0]; // Get today's date in YYYY-MM-DD format

    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const response = await fetch(`http://localhost:5000?date=${selectedDate}`); // Pass date as a query parameter
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setAppointments(data); // Adjust based on the shape of your response
            } catch (error) {
                setError('Error fetching appointments. Please try again later.');
                console.error('Error fetching appointments:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchAppointments();
    }, [selectedDate]);

    return (
        <>
            <Navbar />
            <Container sx={{ mt: 5 }}>
                <Typography variant="h4" gutterBottom>
                    Appointments for {selectedDate}
                </Typography>

                {loading ? (
                    <CircularProgress />
                ) : error ? ( // Display error if it exists
                    <Alert severity="error">{error}</Alert>
                ) : (
                    <List>
                        {appointments.length > 0 ? (
                            appointments.map((appointment) => (
                                <ListItem key={appointment.id}>
                                    <ListItemText
                                        primary={`${appointment.time} - ${appointment.patient.name}`} // Patient's name
                                        secondary={`Age: ${appointment.patient.age}`} // Patient's age
                                    />
                                </ListItem>
                            ))
                        ) : (
                            <Typography variant="body1">No appointments scheduled for this date.</Typography>
                        )}
                    </List>
                )}
            </Container>
        </>
    );
};

export default DoctorDashboard;
