// ManageAppointment.tsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {
    Container,
    Typography,
    Button,
    CircularProgress,
    Alert,
    TextField,
    Box,
    Card,
    CardContent,
} from '@mui/material';

interface Appointment {
    id: number;
    date: string;
    time: string;
    patient: Patient;
}

interface Patient {
    id: string;
    name: string;
    age: number;
}

interface History {
    patientId: string;
    history: string; // Assuming JSON string or object
}

const ManageAppointment: React.FC = () => {
    const { patientId } = useParams<{ patientId: string }>();
    const [patient, setPatient] = useState<Patient | null>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
    const [prescription, setPrescription] = useState<string>('');
    const [successMessage, setSuccessMessage] = useState<string | null>(null);

    useEffect(() => {
        const fetchPatientDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5000/patients/${patientId}`);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setPatient(data);
            } catch (error) {
                setError('Error fetching patient details. Please try again later.');
                console.error('Error fetching patient details:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchPatientDetails();
    }, [patientId]);

    const addPrescription = async () => {
        if (prescription) {
            try {
                const response = await fetch(`http://localhost:5000/prescriptions`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        patientId,
                        prescription,
                    }),
                });
                if (!response.ok) {
                    throw new Error('Failed to add prescription');
                }
                setSuccessMessage('Prescription added successfully.');
                setPrescription('');
            } catch (error) {
                setError('Error adding prescription. Please try again later.');
            }
        }
    };

    const completeAppointment = async () => {
        try {
            const response = await fetch(`http://localhost:5000/appointments/${patientId}/complete`, {
                method: 'POST',
            });
            if (!response.ok) {
                throw new Error('Failed to complete appointment');
            }
            setSuccessMessage('Appointment completed successfully.');
        } catch (error) {
            setError('Error completing appointment. Please try again later.');
        }
    };

    return (
        <Container sx={{ mt: 5 }}>
            {loading ? (
                <CircularProgress />
            ) : error ? (
                <Alert severity="error">{error}</Alert>
            ) : patient ? (
                <Card variant="outlined" sx={{ padding: 2 }}>
                    <CardContent>
                        <Typography variant="h5" gutterBottom>
                            Patient Details
                        </Typography>
                        <Typography variant="body1">Patient ID: {patient.id}</Typography>
                        <Typography variant="body1">Patient Name: {patient.name}</Typography>
                        <Typography variant="body1">Patient Age: {patient.age}</Typography>

                        <Box sx={{ mt: 3 }}>
                            <TextField
                                label="Add Prescription"
                                variant="outlined"
                                fullWidth
                                value={prescription}
                                onChange={(e) => setPrescription(e.target.value)}
                            />
                            <Button variant="contained" onClick={addPrescription} sx={{ mt: 2 }}>
                                Add Prescription
                            </Button>
                        </Box>

                        <Box sx={{ mt: 3 }}>
                            <Button variant="contained" onClick={completeAppointment}>
                                Complete Appointment
                            </Button>
                        </Box>

                        {successMessage && (
                            <Alert severity="success" sx={{ mt: 2 }}>
                                {successMessage}
                            </Alert>
                        )}
                    </CardContent>
                </Card>
            ) : (
                <Typography variant="body1">No details available for this patient.</Typography>
            )}
        </Container>
    );
};

export default ManageAppointment;
