import React, { useEffect, useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, CircularProgress } from '@mui/material';
import Navbar from './Navbar';

interface Appointment {
  id: number;
  time: string;
  patientName: string;
  // Add any other relevant fields
}

const Appointments: React.FC = () => {
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [loading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const response = await fetch('YOUR_BACKEND_API_ENDPOINT'); // Replace with your backend API endpoint
        const data = await response.json();
        setAppointments(data); // Adjust based on the shape of your response
      } catch (error) {
        console.error('Error fetching appointments:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, []);

  return (
    <>
      <Navbar />
      <Container sx={{ mt: 5 }}>
        <Typography variant="h4" gutterBottom>
          Today's Appointments
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : (
          <List>
            {appointments.length > 0 ? (
              appointments.map((appointment) => (
                <ListItem key={appointment.id}>
                  <ListItemText
                    primary={`${appointment.time} - ${appointment.patientName}`} // Customize as needed
                  />
                </ListItem>
              ))
            ) : (
              <Typography variant="body1">No appointments scheduled for today.</Typography>
            )}
          </List>
        )}
      </Container>
    </>
  );
};

export default Appointments;
