import React, { useState } from 'react';
import {
  TextField,
  Button,
  Typography,
  Container,
  Box,
} from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useUser } from './UserContext';

const Login: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { setUser } = useUser();
  const fetchUserRole = async (token:string) => {
    try {
        const userResponse = await fetch('/api/getUserRole', { // Replace with your API endpoint
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json',
            },
        });

        if (userResponse.ok) {
            const userData = await userResponse.json();
            const { role } = userData;

            // Redirect based on the role
            if (role === 'patient') {
                navigate('/patient/dashboard');
            } else if (role === 'doctor') {
                navigate('/doctor/dashboard');
            } else if (role === 'admin') {
                navigate('/admin/dashboard');
            }
        } else {
            setError('Failed to retrieve user role');
        }
    } catch (error) {
        setError('An error occurred while fetching the user role');
        console.error(error);
    }
};
  const handleLogin = async (event: React.FormEvent) => {
    event.preventDefault();

    try {
      const response = await axios.post('/api/login', { username, password });

      if (response.status === 200 && response.data.success) {
        const { token } = response.data; // Assuming the token is returned in the response
        sessionStorage.setItem('jwtToken', token); // Store the JWT token in session storage
    
        // Call an API to get the user information based on the token
        fetchUserRole(token);
    } else {
        setError('Invalid credentials');
    }
    
    } catch (error) {
      console.error('Login error', error);
      setError('Login failed');
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <Box sx={{ mt: 8, mb: 2 }}>
        <Typography component="h1" variant="h5">
          Login
        </Typography>
        {error && <Typography color="error">{error}</Typography>}
        <form onSubmit={handleLogin}>
          <TextField
            margin="normal"
            required
            fullWidth
            label="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            label="Password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            sx={{ mt: 3, mb: 2 }}
          >
            Login
          </Button>
        </form>
      </Box>
    </Container>
  );
};

export default Login;
