import React, { useEffect, useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Button,
  Menu,
  MenuItem,
  Avatar,
  Box,
} from '@mui/material';
import { Home as HomeIcon, ArrowDropDown } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import HealthAndSafetyIcon from '@mui/icons-material/HealthAndSafety';

const PatientNavigation: React.FC = () => {
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const navigate = useNavigate();
  const [patientName,setPatientName] = useState<string|null>(null);

  useEffect(() => {
    const name = sessionStorage.getItem('userName');
    setPatientName(name);
  }, []);

  const handleMenuOpen = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleMenuClick = (path: string) => {
    handleMenuClose();
    navigate(path);
  };

  return (
    <AppBar position="static" color="primary">
      <Toolbar>
        {/* Left side: Logo and Company Name */}
        <HealthAndSafetyIcon />
                    <Typography variant="h6" style={{ flexGrow: 1, marginLeft: 10 }}>
                        HealthWise Hospital Management
                    </Typography>

        {/* Right side: Home button and patient name with dropdown menu */}
        <Button
          color="inherit"
          startIcon={<HomeIcon />}
          onClick={() => navigate('/patient/dashboard')}
        >
          Home
        </Button>

        <IconButton color="inherit" onClick={handleMenuOpen}>
          <Avatar sx={{ marginRight: 1 }} />
          <Typography variant="body1">{patientName}</Typography>
          <ArrowDropDown />
        </IconButton>

        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
          anchorOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
        >
          <MenuItem onClick={() => handleMenuClick('/patient-profile')}>Profile</MenuItem>
          <MenuItem onClick={() => handleMenuClick('/medical-history')}>Medical History</MenuItem>
          <MenuItem onClick={() => handleMenuClick('/logout')}>Logout</MenuItem>
        </Menu>
      </Toolbar>
    </AppBar>
  );
};

export default PatientNavigation;
