import React, { useEffect, useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, CircularProgress, Box, Button } from '@mui/material';
import Navbar from './Navbar';

interface Appointment {
  id: number;
  time: string;
  patientName: string;
  // Add any other relevant fields
}

const Appointments: React.FC = () => {
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [loading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const response = await fetch('YOUR_BACKEND_API_ENDPOINT'); // Replace with your backend API endpoint
        const data = await response.json();
        setAppointments(data); // Adjust based on the shape of your response
      } catch (error) {
        console.error('Error fetching appointments:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, []);

  return (
    <>
      <Navbar />
      <Container>
      <Box sx={{ display: 'flex',
            flexDirection: 'column',
            gap: 2,
            mt: 4,
            width: '100%',
            maxWidth: '400px',
            p: 3,
            bgcolor: '#f0f0f0',  // Light grey background
            borderRadius: 2,
            boxShadow: 1 }} >
          <Button
              variant="contained"
              color="primary"
              fullWidth
              sx={{ borderRadius: '50px' }}
              onClick={() => window.location.href = '/appointments'} // Redirect to appointments page
            >
              Get Appointments
            </Button>
          <Button variant="contained" color="primary" fullWidth sx={{ borderRadius: '50px' }}>
            Mark Prescriptions
          </Button>
          <Button variant="contained" color="primary" fullWidth sx={{ borderRadius: '50px' }}>
            Get History
          </Button>
          <Button variant="contained" color="primary" fullWidth sx={{ borderRadius: '50px' }}>
            Generate Report
          </Button>
        </Box>
        </Container>
    </>
  );
};

export default Appointments;
