import React, { useState } from 'react';
import axios from 'axios';
import { Box, Container, Typography, FormControl, FormLabel, RadioGroup, Radio, FormControlLabel, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import Navbar from './components/PatientNavbar';

const BookAppointment: React.FC = () => {
  const [selectedSymptom, setSelectedSymptom] = useState('');
  const navigate = useNavigate();

  const handleSymptomChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSelectedSymptom(event.target.value);
  };

  const handleSubmit = async () => {
    try {
      const token = sessionStorage.getItem('token');
      
      // Update the patient's symptoms
      await axios.put('/api/patient/update-symptoms', { symptoms: selectedSymptom }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      
      // Redirect to doctor selection page
      navigate('/select-doctor');
    } catch (error) {
      console.error('Error updating symptoms:', error);
    }
  };

  return (
    <div>
      <Navbar />
      <Container>
        <Box mt={4} p={3} boxShadow={3} borderRadius="8px" bgcolor="#f9f9f9" textAlign="center" maxWidth="sm" mx="auto">
          <Typography variant="h5" color="primary" gutterBottom>
            Book an Appointment
          </Typography>
          
          <FormControl component="fieldset" fullWidth>
            <FormLabel component="legend">Select Your Symptoms</FormLabel>
            <RadioGroup value={selectedSymptom} onChange={handleSymptomChange}>
              <FormControlLabel value="Fever" control={<Radio />} label="Fever" />
              <FormControlLabel value="Cold" control={<Radio />} label="Cold" />
              <FormControlLabel value="Headache" control={<Radio />} label="Headache" />
              <FormControlLabel value="Stomach Pain" control={<Radio />} label="Stomach Pain" />
              <FormControlLabel value="Body Ache" control={<Radio />} label="Body Ache" />
            </RadioGroup>
          </FormControl>
          
          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleSubmit} 
            sx={{ mt: 3 }}
            disabled={!selectedSymptom} // Disable button until a symptom is selected
          >
            Proceed to Select Doctor
          </Button>
        </Box>
      </Container>
    </div>
  );
};

export default BookAppointment;
