// ManageAppointment.tsx
import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import {
    Container,
    Typography,
    Button,
    CircularProgress,
    Alert,
    TextField,
    Box,
    Card,
    CardContent,
} from '@mui/material';

interface Appointment {
    id: number;
    date: string;
    time: string;
    patient: Patient;
}

interface Patient {
    id: string;
    name: string;
    age: number;
}

interface History {
    patientId: string;
    history: string; // Assuming JSON string or object
}

const ManageAppointment: React.FC = () => {
    const { appointmentId } = useParams<{ appointmentId: string }>();
    const location = useLocation();
    const query = new URLSearchParams(location.search); // Parse URL query parameters

    // Extract patient information from query parameters
    const patient: Patient = {
        id: query.get('patientId') || '',
        name: query.get('name') || '',
        age: parseInt(query.get('age') || '0', 10),
    };

    const [appointment, setAppointment] = useState<Appointment | null>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
    const [history, setHistory] = useState<History | null>(null);
    const [prescription, setPrescription] = useState<string>('');
    const [successMessage, setSuccessMessage] = useState<string | null>(null);

    useEffect(() => {
        const fetchAppointmentDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5000/appointments/${appointmentId}`);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setAppointment(data);
            } catch (error) {
                setError('Error fetching appointment details. Please try again later.');
                console.error('Error fetching appointment details:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchAppointmentDetails();
    }, [appointmentId]);

    const fetchPatientHistory = async () => {
        if (patient) {
            try {
                const response = await fetch(`http://localhost:5000/patients/${patient.id}/history`);
                if (!response.ok) {
                    throw new Error('Failed to fetch patient history');
                }
                const data = await response.json();
                setHistory(data);
            } catch (error) {
                setError('Error fetching patient history. Please try again later.');
            }
        }
    };

    const addPrescription = async () => {
        if (prescription) {
            try {
                const response = await fetch(`http://localhost:5000/prescriptions`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        appointmentId,
                        prescription,
                    }),
                });
                if (!response.ok) {
                    throw new Error('Failed to add prescription');
                }
                setSuccessMessage('Prescription added successfully.');
                setPrescription('');
            } catch (error) {
                setError('Error adding prescription. Please try again later.');
            }
        }
    };

    const completeAppointment = async () => {
        try {
            const response = await fetch(`http://localhost:5000/appointments/${appointmentId}/complete`, {
                method: 'POST',
            });
            if (!response.ok) {
                throw new Error('Failed to complete appointment');
            }
            setSuccessMessage('Appointment completed successfully.');
        } catch (error) {
            setError('Error completing appointment. Please try again later.');
        }
    };

    return (
        <Container sx={{ mt: 5 }}>
            {loading ? (
                <CircularProgress />
            ) : error ? (
                <Alert severity="error">{error}</Alert>
            ) : appointment ? (
                <Card variant="outlined" sx={{ padding: 2 }}>
                    <CardContent>
                        <Typography variant="h5" gutterBottom>
                            Appointment Details
                        </Typography>
                        <Typography variant="body1">Date: {appointment.date}</Typography>
                        <Typography variant="body1">Time: {appointment.time}</Typography>
                        <Typography variant="body1">Patient Name: {patient.name}</Typography>
                        <Typography variant="body1">Patient Age: {patient.age}</Typography>

                        <Box sx={{ mt: 3 }}>
                            <Button variant="contained" onClick={fetchPatientHistory}>
                                Get Patient History
                            </Button>
                            {history && (
                                <Typography variant="body1" sx={{ mt: 2 }}>
                                    History: {JSON.stringify(history.history)}
                                </Typography>
                            )}
                        </Box>

                        <Box sx={{ mt: 3 }}>
                            <TextField
                                label="Add Prescription"
                                variant="outlined"
                                fullWidth
                                value={prescription}
                                onChange={(e) => setPrescription(e.target.value)}
                            />
                            <Button variant="contained" onClick={addPrescription} sx={{ mt: 2 }}>
                                Add Prescription
                            </Button>
                        </Box>

                        <Box sx={{ mt: 3 }}>
                            <Button variant="contained" onClick={completeAppointment}>
                                Complete Appointment
                            </Button>
                        </Box>

                        {successMessage && (
                            <Alert severity="success" sx={{ mt: 2 }}>
                                {successMessage}
                            </Alert>
                        )}
                    </CardContent>
                </Card>
            ) : (
                <Typography variant="body1">No details available for this appointment.</Typography>
            )}
        </Container>
    );
};

export default ManageAppointment;
