import React, { useEffect, useState } from 'react';
import { AppBar, Toolbar, Typography, Button, Avatar, Menu, MenuItem } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import HealthAndSafetyIcon from '@mui/icons-material/HealthAndSafety';

const PatientNavbar: React.FC = () => {
  const [patientName, setPatientName] = useState<string>(''); // State to hold patient name
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const navigate = useNavigate();

  // Fetch patient data from the API
  useEffect(() => {
    const fetchPatientData = async () => {
      try {
        const response = await fetch('/api/patient'); // Update this to your patient entity API endpoint
        const data = await response.json();
        setPatientName(data.name); // Assuming the API returns an object with a 'name' property
      } catch (error) {
        console.error('Error fetching patient data:', error);
      }
    };

    fetchPatientData();
  }, []);

  // Handle dropdown menu open
  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  // Handle dropdown menu close
  const handleClose = () => {
    setAnchorEl(null);
  };

  // Handle navigation
  const handleLogout = () => {
    // Handle logout logic here (e.g., clear tokens, redirect)
    console.log('Logout');
  };

  return (
    <AppBar position="static">
      <Toolbar>
        {/* Left side: Logo and Application Name */}
        <HealthAndSafetyIcon />
                    <Typography variant="h6" style={{ flexGrow: 1, marginLeft: 10 }}>
                        HealthWise Hospital Management
                    </Typography>
        {/* Right side: Home button, Patient Name and Avatar */}
        <Button color="inherit" onClick={() => navigate('/patient/dashboard')}>
          Home
        </Button>

        <Typography variant="h6" style={{ margin: '0 10px' }}>
          {patientName}
        </Typography>

        <Avatar onClick={handleClick} style={{ cursor: 'pointer' }} />

        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleClose}
        >
          <MenuItem onClick={handleClose}>Update Profile Details</MenuItem>
          <MenuItem onClick={handleClose}>View History</MenuItem>
          <MenuItem onClick={handleLogout}>Logout</MenuItem>
        </Menu>
      </Toolbar>
    </AppBar>
  );
};

export default PatientNavbar;
